import React from "react";

function ResultItemContainer({ gameItem, score }) {
  return gameItem ? (
    <div className="result-item-container">
      <span>{gameItem.name}</span>
      <img src={gameItem.url} className="avatar" alt={gameItem.url} style={gameItem.style}></img>
      <span>{score}</span>
    </div>
  ) : null;
}

function Result({ user1GameItem, user2GameItem, result, userScore, computerScore, winningScore, setResetLayout }) {
  if (userScore === winningScore || computerScore === winningScore)
    setResetLayout(true);

  const resultColor = result === "Win" ? "blue" : result === "Tie" ? "grey" : "red";

  return (
    <div>
      <h2 className="result-header">Result</h2>
      <div className="result-wrapper">
        <ResultItemContainer gameItem={user1GameItem} score={userScore} />
        <span className="result" style={{ backgroundColor: resultColor }}>{result}</span>
        <ResultItemContainer gameItem={{ ...user2GameItem, style: { borderStyle: "dashed" } }} score={computerScore} />
      </div>
    </div>
  );
}

export default Result;
