import React, { useState, useEffect } from "react";
import Result from "./Result";
import Display from "./Display";
import Choices from "./Choices";
import Reset from "./Reset";

import { getRandomGameItem, calculatorUserWinner } from "../utils";

const gameItems = [
  {
    url: "/images/rock-hand.png",
    id: 0,
    winItemIds: [2, 3],
    name: "Rock",
  },
  {
    url: "/images/paper-hand.png",
    id: 1,
    winItemIds: [0, 4],
    name: "Paper",
  },
  {
    url: "/images/scissors-hand.png",
    id: 2,
    winItemIds: [1, 3],
    name: "Scissor",
  },
  {
    url: "/images/lizard-hand.png",
    id: 3,
    winItemIds: [1, 4],
    name: "Lizard",
  },
  {
    url: "/images/spock-hand.png",
    id: 4,
    winItemIds: [0, 2],
    name: "Spock",
  },
];

export default function Main() {
  const [result, setResult] = useState("N/N");
  const [userGameItem, setUserGameItem] = useState(null);
  const [computerGameItem, setComputerGameItem] = useState(null);
  const [userScore, setUserScore] = useState(0);
  const [computerScore, setComputerScore] = useState(0);
  const [winningScore, setWinningScore] = useState(3);
  // Lay Out
  const [initialLayout, setInitialLayout] = useState(true);
  const [countdownLayout, setCountdownLayout] = useState(false);
  const [startGameLayout, setStartGameLayout] = useState(false);
  const [resetLayout, setResetLayout] = useState(false);

  // Handle
  const handleGameItemChange = (gameItem) => {
    setUserGameItem({ ...gameItem });
  };
  // Initial -> Countdown -> Start Game -> Reset
  const handleInitialLayout = () => {
    setInitialLayout(false);
    setCountdownLayout(true);
  };
  const handleCountdownLayout = () => {
    setCountdownLayout(false);
    setStartGameLayout(true);
  };
  const handleStartGameLayout = () => {
    setStartGameLayout(false);
    setResetLayout(true);
  };
  const handleReset = () => {
    setResetLayout(false);
    setInitialLayout(true);
    setUserScore(0);
    setComputerScore(0);
    setResult("N/N");
    setUserGameItem(null);
    setComputerGameItem(null);
  };

  //Use Effect
  useEffect(() => {
    if (userGameItem) {
      const computerNewItem = getRandomGameItem(gameItems);
      setComputerGameItem({ ...computerNewItem });
      const gameResult = calculatorUserWinner(userGameItem, computerNewItem);
      setResult(gameResult);
      if (gameResult === "Win") {
        setUserScore(userScore + 1);
      } else if (gameResult === "Lost") {
        setComputerScore(computerScore + 1);
      }
    }
  }, [userGameItem]);

  const gameStartClick = () => {
    handleInitialLayout();
    handleCountdownLayout();
    const countDown = document.querySelector('.countdown');
    let count = 3;
    const timer = setInterval(() => {
      countDown.textContent = count;
      count--;
      console.log(count);
      if (count < 0) {
        handleStartGameLayout();
        clearInterval(timer);
      }
    }, 1000);
  };

  //Render 
  handleInitialLayout();

  return (
    <div className="container">
      <div className="main">
        <h1>Rock Paper Scissor Lizard Spock</h1>
        <div className="row">
          <img className="rules" src="/images/rules.png" alt="rules" />
          <div className="game-container">
            <Display />

            {/* Initial Layout */}
            {initialLayout && (
              <div className="game-initial">

            <div className="win-number">
                Win by
                 <input type="number" value={winningScore} min="1"  step="1"  
                  onChange={(e) => setWinningScore(e.target.value)}
                />
              </div>
              <button className="start-button" onClick={gameStartClick}>Start</button>
              </div>
            )}

            {/* Countdown Layout */}
            {countdownLayout && <div className="countdown"> </div>}

            {/* Start Game Layout */}
            {startGameLayout && (
              <div className="game-start">
                {/* ... rest of start game layout code ... */}
              </div>
            )}

            {/* Reset Layout */}
            {resetLayout && (
              <div className="reset">
                <Reset handleReset={handleReset} />
              </div>
            )}
          </div>
          <div className="game-end"> </div>
        </div>
      </div>
    </div>
  );
}
