import React from "react";
import Player from "./Player";

function Reset({
  handleReset,
  result,
  userScore,
  computerScore,
  winningScore,
  setResetLayout,
}) {
  if (userScore == winningScore) {
    <div className="reset-layout">
      <Player
        avatarUrl="/avatar/huy.jpeg"
        name="Huy"
        color="black"
        borderStyle="solid"
      />
      <div> Won </div>
      <button className="reset" onClick={handleReset}>
        Reset
      </button>
    </div>;
  } else {
    return (
      <div className="reset-layout">
        <Player
          avatarUrl="/avatar/computer.png"
          name="Computer"
          color="black"
          borderStyle="dashed"
        />

        <div> Won </div>
        <button className="reset" onClick={handleReset}>
          Reset
        </button>
      </div>
    );
  }
}

export default Reset;
